@import "./config";

$filmItemWidth: 150px;

.filmItem{
  width: $filmItemWidth;
  background-color: transparent /* OU QUAND ON MET LE TITRE : $darkgrey */;
  margin: 0 10px;
  cursor: pointer;

  &:hover{
    transform: scale(1.1);
  }

  .filmImg{
    width: $filmItemWidth;

    img{
      width: 100%;
      object-fit: contain;
    }
  }

  .filmTitleContainer{
    @extend .center;
    width: 100%;
    height: 2rem;
    padding: 0 .3rem;
    padding-bottom: .5rem;
    
    .filmTitle{
      width: 100%;
      text-align: center;
      overflow: hidden;
      white-space: nowrap;
    }
  }
}

.filmCategory{
  position: relative;
  margin: 2rem 0;
  padding: 0 $container_size;

  .categoryTitle{
    margin-left: 10px;
  }

  .chevron{
    color: $white;
    opacity: 0;
    font-size: 3rem;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    z-index: 1;
    cursor: pointer;
    background: transparent;

    $chevron_position: 0px;

    svg path{
      pointer-events: none;
    }

    &.chevronLeft{
      left: $chevron_position;
    }
    &.chevronRight{
      right: $chevron_position;
    }
  }

  &:hover{
    .chevron{
      opacity: .3;
      &:hover{
        opacity: 1;
      }
    }
  }

  .filmRow{
    position: relative;
    display: flex;
    overflow-x: scroll;
    padding: 1rem 0;
    transition: .3s;

    &::-webkit-scrollbar{
      display: none;
    }

    &.bigger{
      .filmItem{
        width: $filmItemWidth + 65;

        .filmImg{
          width: $filmItemWidth + 65;
        }
      }
    }
  }

  .singleFilmDetails{
    width: calc(100% - 10px);
    height: 350px;
    background-color: $darkgrey;
    margin-left: 10px;
    border-radius: .25rem;
    padding: 1rem;
    display: grid;
    grid-template-columns: 1fr 1fr;

    .noVideoFound{
      @extend .center;
      width: 100%;
      height: 320px;
      background-color: rgba(#000, .3);
    }

    .details{
      padding-left: 2rem;
      position: relative;

      h3{
        font-size: 1.8rem;
        margin-bottom: 2rem;
      }

      .description{
        font-size: .9rem;
        font-weight: 400;
        line-height: 1.3;
      }

      .genres{
        position: absolute;
        bottom: 0;
        display: flex;
        flex-flow: wrap;

        .genre_tag{
          background-color: rgba(#000, .3);
          padding: .3rem 1rem;
          margin-right: 5px;
          margin-top: 5px;
        }
      }
    }
  }

}


@media screen and (max-width: $bpS){
  .filmCategory{
    .singleFilmDetails{
      grid-template-columns: 1fr;
      height: fit-content;

      .noVideoFound{
        height: unset;
        padding: 1rem 0;
      }

      .details{
        padding: 2rem 0;
      }
    }
  }
}

